styles.css:
:root :where(.wp-block-site-title a) {
    color: inherit;
    text-decoration: none;
}

.castos-player {
  margin-bottom: 30px;
position: fixed;
  width: 95%; height: 150px;
  bottom: 10px; right: 0%; left: 0%;
  margin: auto;
  border: 0px solid green;
  padding: 0px;
z-index: 99999999999999999999999999999999999999999;
}

@media (max-width: 768px) {
    .castos-player .player__artwork img {
        width: 45px;
        height: 45px;
        margin: 12px;
        border-radius: 4px;
        margin-top: -100px;
    }
}

.podcast-content { margin-bottom: 50px; }


.wp-block-column.is-vertically-aligned-bottom, .wp-block-column.is-vertically-aligned-center, .wp-block-column.is-vertically-aligned-top {
    width: 100%;
    border-radius: 20px;
    border: 0px;
}


.wp-block-column.is-vertically-aligned-bottom, .wp-block-column.is-vertically-aligned-center, .wp-block-column.is-vertically-aligned-top:hover {
    width: 100%;
    border-radius: 20px;
border: 0px;

}
.castos-player.dark-mode .player {
    background: linear-gradient(359.68deg, var(--ssp-dark-mode-bg2-color) 1.03%, var(--ssp-dark-mode-bg1-color) 77.9%);
    border-radius: 20px;

}

.has-black-background-color {


    margin-left: auto !important;
    margin-right: auto !important;

}

.has-black-background-color:hover {
      box-shadow: 1px 1px 24px #555;
outline: 2px solid #9C9C9C;
    margin-left: auto !important;
    margin-right: auto !important;

}


.castos-player.light-mode .playlist__wrapper {
    color: #383344;
    background: #dfdfdf;


}
.castos-player.light-mode .player {
    background: linear-gradient(359.68deg, #ddd 1.03%, #f0f0f0 77.9%);
    background: linear-gradient(359.68deg, var(--ssp-light-mode-bg2-color) 1.03%, var(--ssp-light-mode-bg1-color) 77.9%);
    border-radius: 20px;
align-content: center;
}

.castos-player .player__artwork {
    line-height: 0;
    z-index: 0;
    height: 150px;
    width: 150px;
align-content: center;
}



.is-layout-flex > :is(*, div) {
    margin: 0;
    max-width: 270px;
align-content: center;
}
